import {css} from "lit-element"; export const style = css`.thumbtack,.thumbtack .parent-node{position:relative}.parent-thumbtack{display:flex;overflow:hidden;touch-action:none;outline:0}.thumbtack{width:100%;height:100%;user-select:none;perspective:0}.thumbtack .thumbtack-node{display:flex;align-items:center;position:absolute;background:#0ff;width:160px;min-height:40px;border-radius:4px;border:2px solid #000;color:#000;z-index:2;padding:15px}.thumbtack .thumbtack-node.selected{background:red}.thumbtack .thumbtack-node:hover{cursor:move}.thumbtack .thumbtack-node .inputs,.thumbtack .thumbtack-node .outputs{width:0}.thumbtack .thumbtack-node .thumbtack_content_node{width:100%;display:block}.thumbtack .thumbtack-node .input,.thumbtack .thumbtack-node .output{position:relative;width:20px;height:20px;background:#fff;border-radius:50%;border:2px solid #000;cursor:crosshair;z-index:1;margin-bottom:5px}.thumbtack .thumbtack-node .input{left:-27px;top:2px;background:#ff0}.thumbtack .thumbtack-node .output{right:-3px;top:2px}.thumbtack svg{z-index:0;position:absolute;overflow:visible!important}.thumbtack .connection{position:absolute;pointer-events:none;aspect-ratio:1/1}.thumbtack .connection .main-path{fill:none;stroke-width:5px;stroke:#4682b4;pointer-events:all}.thumbtack .connection .main-path:hover{stroke:#1266ab;cursor:pointer}.thumbtack .connection .main-path.selected{stroke:#43b993}.thumbtack .connection .point{cursor:move;stroke:#000;stroke-width:2;fill:#fff;pointer-events:all}.thumbtack .connection .point.selected,.thumbtack .connection .point:hover{fill:#1266ab}.thumbtack .main-path{fill:none;stroke-width:5px;stroke:#4682b4}.thumbtack-delete{position:absolute;display:block;width:30px;height:30px;background:#000;color:#fff;z-index:4;border:2px solid #fff;line-height:30px;font-weight:700;text-align:center;border-radius:50%;font-family:monospace;cursor:pointer}.thumbtack>.thumbtack-delete{margin-left:-15px;margin-top:15px}.parent-node .thumbtack-delete{right:-15px;top:-15px}`;